import React, { useState } from 'react';
import './Multiplier.css';

const Multiplier = ({ multiplier, index }) => {
  const [hit, setHit] = useState(false);

  const triggerEffect = () => {
    setHit(true);
    setTimeout(() => setHit(false), 300);
  };

  return (
    <div
      className={`multiplier ${hit ? 'active' : ''}`}
      style={{ left: index * 50 + 20 }}
      onClick={triggerEffect}
    >
      x{multiplier}
    </div>
  );
};

export default Multiplier;
