import React, { useEffect, useState } from 'react';
import './Ball.css';

const Ball = ({ ball }) => {
  const [position, setPosition] = useState({ x: ball.x, y: ball.y });
  const [hitMultiplier, setHitMultiplier] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setPosition((prev) => ({ x: prev.x + Math.random() * 10 - 5, y: prev.y + 5 }));
      if (position.y > 400 && !hitMultiplier) {
        setHitMultiplier(true);
        setTimeout(() => setHitMultiplier(false), 500);
      }
    }, 50);
    return () => clearInterval(interval);
  }, [position, hitMultiplier]);

  return (
    <div
      className={`ball ${hitMultiplier ? 'hit' : ''}`}
      style={{ left: position.x, top: position.y }}
    ></div>
  );
};

export default Ball;
