import React, { useState, useEffect } from 'react';
import './Multiplier.css';

const Multiplier = ({ multiplier, x, y, onHit }) => {
  const [hit, setHit] = useState(false);

  useEffect(() => {
    if (hit) {
      const timeout = setTimeout(() => setHit(false), 200);
      return () => clearTimeout(timeout);
    }
  }, [hit]);

  const handleHit = () => {
    setHit(true);
    onHit();
  };

  return (
    <div
      className={`multiplier ${hit ? 'hit' : ''}`}
      style={{ left: x, top: y }}
      onClick={handleHit}
    >
      x{multiplier}
    </div>
  );
};

function onBallHitMultiplier(multiplierElement) {
  multiplierElement.classList.add('multiplier-hit');

  // Remove the class after the animation ends to allow it to be re-applied on the next hit
  setTimeout(() => {
    multiplierElement.classList.remove('multiplier-hit');
  }, 500);
}

export default Multiplier;
