import { useEffect, useState } from 'react';

const useBallMovement = (initialX, initialY) => {
  const [position, setPosition] = useState({ x: initialX, y: initialY });
  const [velocity, setVelocity] = useState({ x: 0, y: 5 });

  useEffect(() => {
    const interval = setInterval(() => {
      setPosition((prevPos) => ({
        x: prevPos.x + velocity.x,
        y: prevPos.y + velocity.y,
      }));

      // Simulate random small horizontal velocity for bouncing
      setVelocity((prevVel) => ({
        x: prevVel.x + (Math.random() > 0.5 ? 1 : -1),
        y: prevVel.y,
      }));
    }, 50);

    return () => clearInterval(interval);
  }, [velocity]);

  return position;
};

export default useBallMovement;
