// Inside PlinkoGame.js

// Remove unnecessary imports
import React, { useState, useEffect, useRef, useMemo } from 'react';
import Peg from '../Peg/Peg';
import Multiplier from '../Multiplier/Multiplier';
import './PlinkoGame.css';
import '../Ball/Ball.css';

// Define the peg rows Y-coordinates
const PEG_ROWS_Y = [130, 190, 250, 310, 370, 430, 490, 550, 610];

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);
  const [budget, setBudget] = useState(1000);
  const [ballPrice, setBallPrice] = useState(10);
  const multiplierRefs = useRef([]);

  // Generate pegs
  const pegs = useMemo(() => generateCenteredPyramidPegs(10, 375, 70), []);

  // Generate multipliers
  const multipliers = useMemo(
    () => [
      { multiplier: 8, x: 114, y: 650, id: 0 },
      { multiplier: 4, x: 164, y: 650, id: 1 },
      { multiplier: 2, x: 214, y: 650, id: 2 },
      { multiplier: 1, x: 264, y: 650, id: 3 },
      { multiplier: 0.5, x: 314, y: 650, id: 4 },
      { multiplier: 0.3, x: 364, y: 650, id: 5 },
      { multiplier: 0.5, x: 414, y: 650, id: 6 },
      { multiplier: 1, x: 464, y: 650, id: 7 },
      { multiplier: 2, x: 514, y: 650, id: 8 },
      { multiplier: 4, x: 564, y: 650, id: 9 },
      { multiplier: 8, x: 614, y: 650, id: 10 },
    ],
    []
  );

  const animationRef = useRef();

  useEffect(() => {
    const update = () => {
      setBalls((prevBalls) =>
        prevBalls
          .map((ball) => {
            // Move the ball down by a fixed amount
            let newY = ball.position.y + 5;

            // At each peg row, randomly move left or right
            if (PEG_ROWS_Y.includes(Math.round(newY))) {
              const direction = Math.random() < 0.5 ? -1 : 1; // -1 for left, 1 for right
              const shiftX = direction * 20; // Fixed horizontal shift
              ball.position.x += shiftX;
            }

            // Update the ball's position
            const newPosition = {
              x: ball.position.x,
              y: newY,
            };

            // Ensure the ball stays within the game board boundaries
            if (newPosition.x < 10) newPosition.x = 10;
            if (newPosition.x > 740) newPosition.x = 740;

            // Check for collisions with multipliers
            let hitMultiplier = false;
            multipliers.forEach((mult, index) => {
              const withinX =
                newPosition.x > mult.x && newPosition.x < mult.x + 45;
              const withinY =
                newPosition.y > mult.y && newPosition.y < mult.y + 45;

              if (withinX && withinY && !ball.hitMultiplier) {
                // Update budget
                setBudget(
                  (prev) => prev + ballPrice * mult.multiplier
                );

                // Add animation class
                const multiplierElement = multiplierRefs.current[index];
                if (multiplierElement) {
                  multiplierElement.classList.add('multiplier-hit');
                  setTimeout(() => {
                    multiplierElement.classList.remove('multiplier-hit');
                  }, 500);
                }

                // Mark the ball as hit
                ball.hitMultiplier = true;
                hitMultiplier = true;
              }
            });

            // Remove the ball if it has hit a multiplier or gone off-screen
            if (hitMultiplier || newPosition.y > 900) {
              return null;
            } else {
              return {
                ...ball,
                position: newPosition,
              };
            }
          })
          .filter(Boolean)
      );

      animationRef.current = requestAnimationFrame(update);
    };

    animationRef.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationRef.current);
  }, [multipliers, ballPrice]);

  const addBall = () => {
    if (budget < ballPrice) {
      return;
    }
    setBudget((prev) => prev - ballPrice);
    const newBall = {
      id: Date.now(),
      position: { x: 375, y: 70 }, // Start at the top center
      hitMultiplier: false,
    };
    setBalls((prevBalls) => [...prevBalls, newBall]);
  };

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.code === 'Space') {
        addBall();
      }
    };

    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return (
    <div className="game-container">
      <div className="user-interface">
        <h2>Budget: ${budget.toFixed(2)}</h2>
        <label>
          Price per ball:
          <input
            type="number"
            value={ballPrice}
            onChange={(e) => setBallPrice(Number(e.target.value))}
          />
        </label>
        <button onClick={addBall}>Drop Ball</button>
      </div>
      <div className="game-board">
        <div className="pegs">
          {pegs.map((peg, index) => (
            <Peg key={index} x={peg.x} y={peg.y} />
          ))}
        </div>

        <div className="multipliers">
          {multipliers.map((mult, i) => (
            <Multiplier
              key={i}
              ref={(el) => (multiplierRefs.current[i] = el)}
              multiplier={mult.multiplier}
              x={mult.x}
              y={mult.y}
              id={mult.id}
              onHit={() => {}}
            />
          ))}
        </div>

        {balls.map((ball) => (
          <div
            key={ball.id}
            className="ball"
            style={{
              left: `${ball.position.x}px`,
              top: `${ball.position.y}px`,
            }}
          />
        ))}
      </div>
    </div>
  );
};

// Modify the generateCenteredPyramidPegs function to align with PEG_ROWS_Y
const generateCenteredPyramidPegs = (rows, centerX, startY) => {
  const pegs = [];
  const spacingX = 55;
  const spacingY = 60;

  for (let row = 1; row <= rows; row++) {
    for (let col = 0; col < row; col++) {
      pegs.push({
        x: centerX + (col - (row - 1) / 2) * spacingX,
        y: startY + (row - 1) * spacingY,
      });
    }
  }

  return pegs;
};

export default PlinkoGame;
