import React, { useState } from 'react';
import Ball from './Ball';
import Peg from './Peg';
import Multiplier from './Multiplier';
import './PlinkoGame.css';

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);
  const [budget, setBudget] = useState(1000);
  const [ballPrice, setBallPrice] = useState(10);

  // Function to add a new ball to the game
  const addBall = () => {
    if (budget < ballPrice) {
      alert("Not enough budget to drop a ball!");
      return;
    }
    setBudget(prev => prev - ballPrice);
    const newBall = { id: balls.length, x: 200, y: 0 }; // Drops from the center
    setBalls([...balls, newBall]);
  };

  // Handle multiplier hit to update budget
  const handleMultiplierHit = (multiplier) => {
    setBudget(prev => prev + ballPrice * multiplier);
  };

  return (
    <div className="game-board">
      <h2>Budget: ${budget}</h2>
      <label>
        Price per ball: 
        <input
          type="number"
          value={ballPrice}
          onChange={(e) => setBallPrice(Number(e.target.value))}
        />
      </label>
      <button onClick={addBall}>Drop Ball</button>

      <div className="pegs">
        {generatePyramidPegs(6, 50, 50).map((peg, index) => (
          <Peg key={index} x={peg.x} y={peg.y} />
        ))}
      </div>

      <div className="multipliers">
        {[1, 2, 5, 10, 2, 1].map((multiplier, i) => (
          <Multiplier
            key={i}
            multiplier={multiplier}
            index={i}
            onHit={() => handleMultiplierHit(multiplier)}
          />
        ))}
      </div>

      {balls.map((ball) => (
        <Ball key={ball.id} ball={ball} />
      ))}
    </div>
  );
};

// Generate coordinates for pegs in a pyramid shape
const generatePyramidPegs = (rows, startX, startY) => {
  const pegs = [];
  const spacingX = 40; // Horizontal spacing
  const spacingY = 50; // Vertical spacing

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col <= row; col++) {
      pegs.push({
        x: startX + col * spacingX - (row * spacingX) / 2,
        y: startY + row * spacingY,
      });
    }
  }

  return pegs;
};

export default PlinkoGame;
