import { useEffect, useState } from 'react';

const useBallMovement = (initialX, initialY) => {
  const [position, setPosition] = useState({ x: initialX, y: initialY });
  const [velocity, setVelocity] = useState({ x: 0, y: 5 });
  const pegs = generatePyramidPegs(6, 200, 50);

  useEffect(() => {
    const interval = setInterval(() => {
      setPosition((prevPos) => {
        let newX = prevPos.x + velocity.x;
        let newY = prevPos.y + velocity.y;

        let collided = false;

        for (let peg of pegs) {
          const dx = newX - peg.x;
          const dy = newY - peg.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          if (distance < 15) {
            collided = true;
            setVelocity((prevVel) => ({
              x: -prevVel.x * 0.5 + (Math.random() > 0.5 ? 1 : -1), // Reduced bounce
              y: prevVel.y * 0.5 + 2, // Simulates downward flow
            }));
            break;
          }
        }

        return collided ? { x: newX, y: newY } : { x: newX, y: newY + 1 };
      });

      setVelocity((prevVel) => ({ x: prevVel.x, y: prevVel.y + 0.5 })); // Gravity effect
    }, 30);

    return () => clearInterval(interval);
  }, [velocity]);

  return position;
};

const generatePyramidPegs = (rows, centerX, startY) => {
  const pegs = [];
  const spacingX = 40;
  const spacingY = 50;

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col <= row; col++) {
      pegs.push({
        x: centerX + (col - row / 2) * spacingX,
        y: startY + row * spacingY,
      });
    }
  }

  return pegs;
};

export default useBallMovement;
