import React, { useEffect } from 'react';
import useBallMovement from '../../hooks/useBallMovement';
import './Ball.css';

const Ball = ({ ball }) => {
  const position = useBallMovement(ball.x, ball.y);

  // Check if ball reaches the multiplier area
  useEffect(() => {
    if (position.y >= 600 && !ball.hitMultiplier) {
      ball.onHitMultiplier();
    }
  }, [position.y, ball]);

  return (
    <div
      className="ball"
      style={{
        left: position.x,
        top: position.y,
        opacity: ball.hitMultiplier ? 0.6 : 1,
      }}
    />
  );
};

export default Ball;
