import React, { useState, useEffect } from 'react';
import Ball from '../Ball/Ball';
import Peg from '../Peg/Peg';
import Multiplier from '../Multiplier/Multiplier';
import './PlinkoGame.css';

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);
  const [budget, setBudget] = useState(1000);
  const [ballPrice, setBallPrice] = useState(10);

  const addBall = () => {
    if (budget < ballPrice) {
      return;
    }
    setBudget((prev) => prev - ballPrice);
    const newBall = { id: balls.length, x: 375, y: 0 };
    setBalls([...balls, newBall]);
  };

  useEffect(() => {
    const handleKeyUp = (event) => {
      if (event.code === 'Space') {
        addBall();
      }
    };

    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [budget, ballPrice, balls]);

  const handleMultiplierHit = (multiplier) => {
    setBudget((prev) => prev + ballPrice * multiplier);

    // get multiplier element
    const multiplierElement = document.querySelector('.multiplier');
    multiplierElement.classList.add('multiplier-hit');

    setTimeout(() => {
      multiplierElement.classList.remove('multiplier-hit');
    }, 500);
  };

  const removeBall = (id) => {
    setBalls((prevBalls) => prevBalls.filter((ball) => ball.id !== id));
  };

  const multipliers = [
    { multiplier: 8, x: 50, y: 650 },
    { multiplier: 4, x: 130, y: 650 },
    { multiplier: 2, x: 210, y: 650 },
    { multiplier: 0.5, x: 290, y: 650 },
    { multiplier: 0.5, x: 370, y: 650 },
    { multiplier: 2, x: 450, y: 650 },
    { multiplier: 4, x: 530, y: 650 },
    { multiplier: 8, x: 610, y: 650 }
  ];

  return (
    <div className="game-board">
      <h2>Budget: ${budget.toFixed(2)}</h2>
      <label>
        Price per ball:
        <input
          type="number"
          value={ballPrice}
          onChange={(e) => setBallPrice(Number(e.target.value))}
        />
      </label>
      <button onClick={addBall}>Drop Ball</button>

      <div className="pegs">
        {generateCenteredPyramidPegs(10, 375, 70).map((peg, index) => (
          <Peg key={index} x={peg.x} y={peg.y} />
        ))}
      </div>

      <div className="multipliers">
        {multipliers.map((mult, i) => (
          <Multiplier
            key={i}
            multiplier={mult.multiplier}
            x={mult.x}
            y={650} // Position for bottom row
            onHit={() => handleMultiplierHit(mult.multiplier)}
          />
        ))}
      </div>

      {balls.map((ball) => (
        <Ball
          key={ball.id}
          ball={ball}
          multipliers={multipliers}
          onMultiplierHit={handleMultiplierHit}
          removeBall={removeBall}
        />
      ))}
    </div>
  );
};

// Center the pyramid of pegs
const generateCenteredPyramidPegs = (rows, centerX, startY) => {
  const pegs = [];
  const spacingX = 45;
  const spacingY = 60;

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col <= row; col++) {
      pegs.push({
        x: centerX + (col - row / 2) * spacingX,
        y: startY + row * spacingY,
      });
    }
  }

  return pegs;
};

export default PlinkoGame;
