import React, { useEffect, useState } from 'react';
import useBallMovement from '../../hooks/useBallMovement';
import './Ball.css';

const Ball = ({ ball, multipliers, onMultiplierHit }) => {
  const position = useBallMovement(ball.x, ball.y);
  const [hitMultiplier, setHitMultiplier] = useState(false);

  useEffect(() => {
    if (!hitMultiplier && multipliers.length > 0) {
      multipliers.forEach((mult) => {
        const withinX = position.x >= mult.x && position.x <= mult.x + 60; // Multiplier width
        const withinY = position.y >= mult.y && position.y <= mult.y + 60; // Multiplier height

        console.log('Position:', position.x, position.y);

        if (withinX && withinY) {
          setHitMultiplier(true); // Prevent re-trigger
          onMultiplierHit(mult.multiplier);
          console.log('Hit multiplier:', mult.multiplier);
        }
      });
    }
  }, [position, multipliers, onMultiplierHit, hitMultiplier]);

  return (
    <div
      className={`ball ${hitMultiplier ? 'hit' : ''}`}
      style={{
        left: position.x,
        top: position.y,
      }}
    />
  );
};

export default Ball;
