import React, { useEffect } from 'react';
import './Ball.css';

const Ball = ({ ball, multipliers, onMultiplierHit, removeBall }) => {
  useEffect(() => {
    const interval = setInterval(() => {
      // Update ball position logic here
      // Assuming ball.y is updated somewhere in this interval

      // Check if ball hits the withinY range
      if (ball.y >= 650) {
        const hitMultiplier = multipliers.find(
          (mult) => ball.x >= mult.x && ball.x <= mult.x + 80 // Assuming 80 is the width of the multiplier
        );
        if (hitMultiplier) {
          onMultiplierHit(hitMultiplier.multiplier);
        }
        removeBall(ball.id);
      }
    }, 100);

    return () => clearInterval(interval);
  }, [ball, multipliers, onMultiplierHit, removeBall]);

  return <div className="ball" style={{ left: ball.x, top: ball.y }} />;
};

export default Ball;
