import { useEffect, useState } from 'react';

const useBallMovement = (initialX, initialY) => {
  // Reduced random initial horizontal velocity
  const initialVelocityX = (Math.random() - 0.5) * 1; // From -0.25 to 0.25

  const [position, setPosition] = useState({ x: initialX, y: initialY });
  const [velocity, setVelocity] = useState({ x: initialVelocityX, y: 0 });
  const acceleration = { x: 0, y: 0.98 }; // Gravity
  const pegs = generateCenteredPyramidPegs(10, 375, 70);

  useEffect(() => {
    const interval = setInterval(() => {
      // Update velocity with acceleration
      setVelocity((prevVel) => ({
        x: prevVel.x + acceleration.x,
        y: prevVel.y + acceleration.y,
      }));

      // Update position with velocity
      setPosition((prevPos) => ({
        x: prevPos.x + velocity.x,
        y: prevPos.y + velocity.y,
      }));

      // Detect collision with pegs
      pegs.forEach((peg) => {
        const dx = position.x - peg.x;
        const dy = position.y - peg.y;
        const distance = Math.hypot(dx, dy);
        const minDistance = 15; // Ball radius + peg radius

        if (distance < minDistance) {
          // Calculate normal vector
          const normalX = dx / distance;
          const normalY = dy / distance;

          // Calculate relative velocity
          const relVelX = velocity.x;
          const relVelY = velocity.y;

          // Calculate speed in normal direction
          const speed = relVelX * normalX + relVelY * normalY;

          // Reflect velocity
          const newVelX = velocity.x - 2 * speed * normalX;
          const newVelY = velocity.y - 2 * speed * normalY;

          // Apply damping factor
          setVelocity({
            x: newVelX * 0.2,
            y: newVelY * 0.2,
          });

          // Adjust position to prevent sticking
          setPosition({
            x: position.x + normalX * (minDistance - distance + 0.5),
            y: position.y + normalY * (minDistance - distance + 0.5),
          });
        }
      });

      // Check for wall collisions
      if (position.x < 10 || position.x > 740) {
        setVelocity((prevVel) => ({
          x: -prevVel.x * 0.8,
          y: prevVel.y,
        }));
        setPosition((prevPos) => ({
          x: position.x < 10 ? 10 : 740,
          y: prevPos.y,
        }));
      }
    }, 16); // ~60fps

    return () => clearInterval(interval);
  }, [position, velocity, pegs]);

  return position;
};

const generateCenteredPyramidPegs = (rows, centerX, startY) => {
  const pegs = [];
  const spacingX = 45;
  const spacingY = 60;

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col <= row; col++) {
      pegs.push({
        x: centerX + (col - row / 2) * spacingX,
        y: startY + row * spacingY,
      });
    }
  }

  return pegs;
};

export default useBallMovement;
