import React, { useState } from 'react';
import Ball from '../Ball/Ball';
import Peg from '../Peg/Peg';
import Multiplier from '../Multiplier/Multiplier';
import './PlinkoGame.css';

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);
  const [budget, setBudget] = useState(1000);
  const [ballPrice, setBallPrice] = useState(10);

  const addBall = () => {
    if (budget < ballPrice) {
      alert("Not enough budget to drop a ball!");
      return;
    }
    setBudget((prev) => prev - ballPrice);
    const newBall = { id: balls.length, x: 375, y: 0 }; // Drop from top center
    setBalls([...balls, newBall]);
  };

  const handleMultiplierHit = (multiplier, ballId) => {
    // Update the budget with the multiplier effect
    setBudget((prev) => prev + ballPrice * multiplier);
    // Add a visual effect when the multiplier is hit
    setBalls((prevBalls) =>
      prevBalls.map((ball) =>
        ball.id === ballId ? { ...ball, hitMultiplier: true } : ball
      )
    );
  };

  return (
    <div className="game-board">
      <h2>Budget: ${budget}</h2>
      <label>
        Price per ball:
        <input
          type="number"
          value={ballPrice}
          onChange={(e) => setBallPrice(Number(e.target.value))}
        />
      </label>
      <button onClick={addBall}>Drop Ball</button>

      <div className="pegs">
        {generateCenteredPyramidPegs(10, 375, 70).map((peg, index) => (
          <Peg key={index} x={peg.x} y={peg.y} />
        ))}
      </div>

      <div className="multipliers">
        {[8, 0.5, 0.5, 8].map((multiplier, i) => (
          <Multiplier
            key={i}
            multiplier={multiplier}
            x={150 + i * 150}
            y={600} // Position at the bottom of the board
            onHit={() => handleMultiplierHit(multiplier)}
          />
        ))}
      </div>

      {balls.map((ball) => (
        <Ball key={ball.id} ball={ball} />
      ))}
    </div>
  );
};

// Generate a wider and centered pyramid of pegs
const generateCenteredPyramidPegs = (rows, centerX, startY) => {
  const pegs = [];
  const spacingX = 45;
  const spacingY = 60;

  for (let row = 0; row < rows; row++) {
    for (let col = 0; col <= row; col++) {
      pegs.push({
        x: centerX + (col - row / 2) * spacingX,
        y: startY + row * spacingY,
      });
    }
  }

  return pegs;
};

export default PlinkoGame;
