import React, { useState } from 'react';
import Ball from '../Ball/Ball';
import Peg from './Peg';
import Multiplier from './Multiplier';
import './PlinkoGame.css';

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);

  const addBall = () => {
    const newBall = { id: balls.length, x: Math.random() * 300 + 50, y: 0 };
    setBalls([...balls, newBall]);
  };

  return (
    <div className="game-board">
      <button onClick={addBall}>Drop Ball</button>
      <div className="pegs">
        {Array.from({ length: 50 }).map((_, i) => (
          <Peg key={i} x={Math.random() * 300 + 50} y={i * 20 + 50} />
        ))}
      </div>
      <div className="multipliers">
        {[1, 2, 5, 10, 2, 1].map((multiplier, i) => (
          <Multiplier key={i} multiplier={multiplier} index={i} />
        ))}
      </div>
      {balls.map((ball) => (
        <Ball key={ball.id} ball={ball} />
      ))}
    </div>
  );
};

export default PlinkoGame;
