import React, { useState, useEffect } from 'react';
import Ball from '../Ball/Ball';
import Multiplier from '../Multiplier/Multiplier';
import './PlinkoGame.css';

const PlinkoGame = () => {
  const [balls, setBalls] = useState([]);
  const [budget, setBudget] = useState(100); // Example initial budget
  const ballPrice = 10; // Example ball price
  const multipliers = [
    { x: 100, y: 700, multiplier: 2 },
    { x: 300, y: 700, multiplier: 3 },
    { x: 500, y: 700, multiplier: 5 },
  ];

  const addBall = () => {
    if (budget >= ballPrice) {
      setBalls([...balls, { x: 375, y: 0 }]); // Example starting position
      setBudget(budget - ballPrice);
    }
  };

  const handleMultiplierHit = (multiplier) => {
    setBudget(budget + ballPrice * multiplier);
  };

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.code === 'Space') {
        addBall();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [budget]);

  return (
    <div className="game-board">
      <div className="pegs">
        {/* Render pegs here */}
      </div>
      <div className="multipliers">
        {multipliers.map((mult, index) => (
          <Multiplier
            key={index}
            multiplier={mult.multiplier}
            x={mult.x}
            y={mult.y}
            onHit={() => handleMultiplierHit(mult.multiplier)}
          />
        ))}
      </div>
      {balls.map((ball, index) => (
        <Ball
          key={index}
          ball={ball}
          multipliers={multipliers}
          onMultiplierHit={handleMultiplierHit}
        />
      ))}
      <div className="budget">
        Budget: ${budget}
      </div>
      {budget < ballPrice && (
        <div className="notification">
          Out of budget!
        </div>
      )}
    </div>
  );
};

export default PlinkoGame;
